import React,{useState} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Header from './Header';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
    paper: {
    // width:'60vw',
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    // padding: '50px 50px',
    
        
    
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
    const classes = useStyles();
    
    const [email, setEmail] = useState('');
    const [userName, setUserName] = useState('');
    const [name, setName] = useState('');
    const [password1, setPassword1] = useState('');
    const [password2, setPassword2] = useState('');
    const [date, setDate] = useState('');

    const paperStyle={padding:30,margin:'10vh auto',borderRadius:'10px',backgroundColor:'#F2F7F7'}
    function handleDate(e){
        setDate(e.target.data)
    }
    function handleName(e){
        setName(e.target.data)
    }
    function handleEmail(e) {
        e.preventDefault()
        setEmail(e.target.data)
    }
    function handleUserName(e){
        setUserName(e.target.data)
    }
    function handlePassword1(e){
        setPassword1(e.target.data)
    }
    function handlePassword2(e){
        setPassword2(e.target.data)
    }
    
    return (
        <div style={{ backgroundColor: "#DCDCDC", height:"150vh", marginTop: '0' }}>
            <Header></Header>
            <paper elevation={10} >
            <Container component="main" maxWidth="xs" style={paperStyle}>
                <CssBaseline />
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                    Sign Up
                    </Typography>
                    <div className={classes.form} noValidate>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            placeholder="Email"
                            // name="email"
                            autoComplete="email"
                            value={email}
                            autoFocus
                            onChange={handleEmail}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="userName"
                            placeholder="Enter User Name"
                            name="userName"
                            autoComplete="userName"
                            autoFocus
                            value={userName}
                            onChange={handleUserName}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="name"
                            placeholder="Enter your Name"
                            name="name"
                            autoComplete="name"
                            autoFocus
                            value={name}
                            onChange={handleName}
                        />
                        <TextField
                            id="date"
                            placeholder="Birthday"
                            type="date"
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            defaultValue="1998-09-27"
                           
                            InputLabelProps={{
                            shrink: true,
                                }}
                            value={date}
                            onChange={handleDate}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="password"
                            placeholder="Enter Password"
                            type="password"
                            id="password"
                            autoComplete="current-password"
                            value={password1}
                            onChange={handlePassword1}
                            />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="password2"
                            placeholder="Enter Password again"
                            type="password"
                            id="password2"
                            autoComplete="current-password"
                            value={password2}
                            onChange={handlePassword2}
                            />
                            
                        
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            className={classes.submit}
                        >
                            Sign Up
                        </Button>
                        <Grid container>

                            <Grid item>
                            <Link href="/login" variant="body2">
                                {"Already have an account? Sign In"}
                            </Link>
                            </Grid>
                        </Grid>
                    </div>
                </div>
                <Box mt={8}>
                    <Copyright />
                </Box>
            </Container>
            </paper>
        </div>
  );
}
