import React,{useState,useEffect} from 'react';
import { makeStyles,withStyles} from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { useLocation } from 'react-router-dom';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import Button from '@material-ui/core/Button';
import Header from './Header';



const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '70vw',
    },
    },
    table: {
        
        // width:'70vw'
    },
    tableContainer: {
        margin: 'auto auto',
        width:'70vw'
        
    },
    tableHead:{
        // backgroundColor:'grey'
    }
    
    
}));
const StyledTableCell = withStyles((theme) => ({
  head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
        // width:'70vw'
  },
  body: {
      fontSize: 14, 
    //   width:'70vw'
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
        },
    //   width:'70vw'
  },
}))(TableRow);



function Todo(props) {
    const [rows, setRows] = useState([
        { "name": "Frozen yoghurt", "index": 0 },
        { "name": "Ice cream sandwich", "index": 1 },
        { "name": "Eclair", "index": 2 },
        { "name": "Cupcake", "index": 3 },
        { "name": "Gingerbread", "index": 4 },
        
    ]);
    const [formValue,setFormValue]=useState('')
    const [rerender, setRerender] = useState(false);
    const [newIndex, setNewIndex] = useState(5);
    const [posToEdit, setPosToEdit] = useState({ "pos": -1, "index": -1 });
    const location=useLocation()
    console.log(location.state)
    const handleDelete = (index) => {
        let tempRow = rows;
        for (let i = 0; i < tempRow.length; i++) {
            if (tempRow[i].index === index) {
                tempRow.splice(i, 1);
            }
        }
        setRows(tempRow)
        setRerender(!rerender)
        
    }
    const handleEdit = (index) => {
        let pos = -1;
        for (let i = 0; i < rows.length; i++) {
            if (rows[i].index === index) {
               pos=i;
            }
        }
        setFormValue(rows[pos].name)    
        setPosToEdit({ "pos": pos, "index": index })
    }
    function handleFormValue(event) {
        // event.preventDefault();
        console.log(event)
        setFormValue(event.target.value);
        if (event.key === 'enter') {
            console.log('enter clicked',event.target.value)
        }
    }
    function handleFormSubmit(e) {
        console.log("click hua")
        let tempRow = rows;
        if (posToEdit.pos == -1) {
            tempRow.push({ "name": formValue, "index": newIndex });
            setNewIndex(newIndex + 1);
        }
        else {
            tempRow[posToEdit.pos] = { "name": formValue, "index": posToEdit.index };
            setPosToEdit({ "pos": -1, "index": -1 });
        }
        setRows(tempRow);
        setFormValue('');
        setRerender(!rerender);
    }
    function handleFormCancel(e) {
        setFormValue('');
        setPosToEdit({ "pos": -1, "index": -1 })
    }
    const classes = useStyles();
    return (
        <div>
            <Header val="Logout"></Header>
            <br></br>
            <br></br>
            <div className={classes.root} noValidate autoComplete="off">
               
                <TextField id="filled-basic" label="Enter List Item.. " variant="filled" value={formValue} onChange={handleFormValue}  />
                <br></br>
                <Button variant="contained" color="primary" style={{width:'80px',marginRight:'5px'}} onClick={handleFormSubmit}>
                    Submit
                </Button>
                <Button variant="contained" color="secondary" style={{width:'80px'}} onClick={handleFormCancel}>
                    Clear
                </Button>

                
            </div>
            <br></br>
            <br></br>
            <br></br>
            <TableContainer component={Paper} className={classes.tableContainer} style={{marginBottom:'20px'}} >
                <Table className={classes.table} aria-label="customized table">
                    <TableHead style={{backgroundColor:'grey',color:'white'}}>
                    <TableRow >
                        <StyledTableCell>List Item</StyledTableCell>
                        <StyledTableCell  align='right' style={{marginRight:'5px',marginLeft:'auto'}}></StyledTableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                    {rows.map((row) => (
                        <StyledTableRow key={row.index}>
                            <StyledTableCell component="th" scope="row">
                                {row.name}
                            </StyledTableCell>
                        
                            <StyledTableCell align='right' >
                                
                                <IconButton aria-label="edit"  color="primary" onClick={()=> handleEdit(row.index)}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton aria-label="delete" color="danger" onClick={() => 
                                    handleDelete(row.index)
                                }>
                                    <DeleteIcon />
                                </IconButton>
                            </StyledTableCell>
                        
                        </StyledTableRow>
                    ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
}

export default Todo;
